mapper.update:
UPDATE t_exam
SET exam_name = #{examName},
    exam_date = #{examDate},
    reminder = #{reminder}
WHERE exam_id = #{examId} AND user_id = #{userId}

mapper.select:
SELECT exam_id, user_id, exam_name, exam_date, reminder, create_at
FROM t_exam
WHERE exam_id = #{examId} AND user_id = #{userId}
